import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { analyticsAPI } from '../services/api';
import { toast } from 'react-toastify';
import './Dashboard.css';

const Dashboard = () => {
  const [stats, setStats] = useState(null);
  const [aiReview, setAiReview] = useState(null);
  const [loading, setLoading] = useState(true);
  const [reviewPeriod, setReviewPeriod] = useState('weekly');

  useEffect(() => {
    loadDashboard();
  }, [reviewPeriod]);

  const loadDashboard = async () => {
    try {
      setLoading(true);
      const [statsRes, reviewRes] = await Promise.all([
        analyticsAPI.getSummary(),
        analyticsAPI.getAIReview(reviewPeriod)
      ]);

      setStats(statsRes.data.stats);
      setAiReview(reviewRes.data.review);
    } catch (error) {
      toast.error('Failed to load dashboard');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  if (!stats) {
    return (
      <div className="dashboard">
        <div className="empty-state">
          <h2>Welcome to Your Trading Journal! üéØ</h2>
          <p>Start logging your trades to unlock AI-powered insights</p>
          <Link to="/trades/new" className="btn-primary">
            Add Your First Trade
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Dashboard</h1>
        <Link to="/trades/new" className="btn-primary">+ Add Trade</Link>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-label">Total Trades</div>
          <div className="stat-value">{stats.totalTrades}</div>
        </div>

        <div className="stat-card success">
          <div className="stat-label">Win Rate</div>
          <div className="stat-value">{stats.winRate}%</div>
        </div>

        <div className="stat-card danger">
          <div className="stat-label">Loss Rate</div>
          <div className="stat-value">{stats.lossRate}%</div>
        </div>

        <div className={`stat-card ${stats.netPips >= 0 ? 'success' : 'danger'}`}>
          <div className="stat-label">Net Pips</div>
          <div className="stat-value">
            {stats.netPips >= 0 ? '+' : ''}{stats.netPips}
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Avg R:R Ratio</div>
          <div className="stat-value">{stats.avgRR}</div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Execution Score</div>
          <div className="stat-value">{stats.avgExecutionScore}/10</div>
        </div>
      </div>

      {/* Performance Insights */}
      <div className="insights-section">
        <div className="insight-card">
          <h3>üéØ Best Strategy</h3>
          <p>{stats.bestStrategy}</p>
        </div>

        <div className="insight-card warning">
          <h3>‚ö†Ô∏è Common Mistake</h3>
          <p>{stats.commonMistake}</p>
        </div>
      </div>

      {/* AI Review Section */}
      {aiReview && (
        <div className="ai-review-section">
          <div className="section-header">
            <h2>ü§ñ AI Performance Review</h2>
            <select 
              value={reviewPeriod} 
              onChange={(e) => setReviewPeriod(e.target.value)}
              className="period-select"
            >
              <option value="weekly">Weekly</option>
              <option value="monthly">Monthly</option>
            </select>
          </div>

          <div className="ai-review-grid">
            <div className="review-card">
              <h4>Performance Summary</h4>
              <ul>
                <li><strong>Trades:</strong> {aiReview.tradeCount}</li>
                <li><strong>Win Rate:</strong> {aiReview.winRate}%</li>
                <li><strong>Net Pips:</strong> {aiReview.netPips}</li>
                <li><strong>Avg R:R:</strong> {aiReview.avgRR}</li>
              </ul>
            </div>

            <div className="review-card">
              <h4>Top Performers</h4>
              <ul>
                <li><strong>Strategy:</strong> {aiReview.bestStrategy}</li>
                <li><strong>Session:</strong> {aiReview.bestSession}</li>
                <li><strong>Pair:</strong> {aiReview.bestPair}</li>
              </ul>
            </div>

            <div className="review-card">
