import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav style={{
      background: '#1e293b',
      padding: '1rem 2rem',
      borderBottom: '1px solid #475569',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      position: 'sticky',
      top: 0,
      zIndex: 100
    }}>
      <div style={{ display: 'flex', gap: '2rem', alignItems: 'center' }}>
        <Link to="/dashboard" style={{ 
          color: 'white', 
          textDecoration: 'none', 
          fontWeight: 700,
          fontSize: '1.2rem',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem'
        }}>
          ðŸ“Š Forex Journal
        </Link>
        <Link to="/trades" style={{ color: '#cbd5e1', textDecoration: 'none', fontWeight: 500 }}>
          Trades
        </Link>
        <Link to="/analytics" style={{ color: '#cbd5e1', textDecoration: 'none', fontWeight: 500 }}>
          Analytics
        </Link>
      </div>
      <div style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
        <span style={{ color: '#94a3b8', fontSize: '0.9rem' }}>ðŸ‘¤ {user?.name}</span>
        <button onClick={handleLogout} style={{
          background: '#334155',
          color: 'white',
          border: 'none',
          padding: '0.5rem 1rem',
          borderRadius: '0.375rem',
          cursor: 'pointer',
          fontWeight: 500,
          transition: 'background 0.2s'
        }}
        onMouseEnter={(e) => e.target.style.background = '#475569'}
        onMouseLeave={(e) => e.target.style.background = '#334155'}
        >
          Logout
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
