import React, { useState, useEffect } from 'react';
import { analyticsAPI } from '../services/api';
import { toast } from 'react-toastify';

const Analytics = () => {
  const [trends, setTrends] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAnalytics();
  }, []);

  const loadAnalytics = async () => {
    try {
      const response = await analyticsAPI.getTrends();
      setTrends(response.data.trends);
    } catch (error) {
      toast.error('Failed to load analytics');
    } finally {
      setLoading(false);
    }
  };

  const handleExport = async () => {
    try {
      const response = await analyticsAPI.export();
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `trades-${Date.now()}.csv`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      toast.success('Trades exported successfully');
    } catch (error) {
      toast.error('Failed to export trades');
    }
  };

  if (loading) return <div className="loading-container"><div className="spinner"></div></div>;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '2rem' }}>
        <h1>Analytics</h1>
        <button onClick={handleExport} className="btn-primary">
          Export CSV
        </button>
      </div>

      <div className="card">
        <h3>Performance Trends</h3>
        {trends.length === 0 ? (
          <p>Not enough data yet. Keep trading!</p>
        ) : (
          <table>
            <thead>
              <tr>
                <th>Week</th>
                <th>Win Rate</th>
                <th>Total Pips</th>
                <th>Trades</th>
              </tr>
            </thead>
            <tbody>
              {trends.map((trend, idx) => (
                <tr key={idx}>
                  <td>{trend.week}</td>
                  <td>{trend.winRate}%</td>
                  <td style={{ color: trend.totalPips >= 0 ? 'var(--success)' : 'var(--danger)' }}>
                    {trend.totalPips >= 0 ? '+' : ''}{trend.totalPips}
                  </td>
                  <td>{trend.trades}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Analytics;
