import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Configure axios
axios.defaults.baseURL = API_URL;

// Trade API
export const tradeAPI = {
  getAll: (params) => axios.get('/trades', { params }),
  getById: (id) => axios.get(`/trades/${id}`),
  create: (tradeData) => axios.post('/trades', tradeData),
  update: (id, tradeData) => axios.put(`/trades/${id}`, tradeData),
  delete: (id) => axios.delete(`/trades/${id}`),
  batchDelete: (tradeIds) => axios.delete('/trades/batch/delete', { data: { tradeIds } })
};

// Analytics API
export const analyticsAPI = {
  getSummary: () => axios.get('/analytics/summary'),
  getAIReview: (period) => axios.get('/analytics/ai-review', { params: { period } }),
  export: () => axios.get('/analytics/export', { responseType: 'blob' }),
  getTrends: () => axios.get('/analytics/trends')
};

// Auth API  
export const authAPI = {
  login: (email, password) => axios.post('/auth/login', { email, password }),
  register: (name, email, password) => axios.post('/auth/register', { name, email, password }),
  getProfile: () => axios.get('/auth/me')
};

export default axios;
